package com.bridgelabz;

public class Vowels {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'o';

        if(ch == 'a' || ch == 'e' || ch == 'i' || ch == 'o' || ch == 'u' )
            System.out.println("Alphabet " +ch+ " is vowel");
        else
            System.out.println("Alphabet " +ch+ " is consonant");
	}
}